-- Contains a copy of all asset data.
CREATE TABLE assets (
    -- Asset ID
    id SERIAL PRIMARY KEY,
    -- Organization ID this asset belongs to.
    org_id UUID NOT NULL,
    -- Asset type, one of [domain, service]
    type TEXT NOT NULL,
    -- JSON data with asset details
    details JSONB NOT NULL
);

-- Enable RLS for policies.
ALTER TABLE assets ENABLE ROW LEVEL SECURITY;

-- Restrict viewing the table to a specific org_id based on the session initialization.
CREATE POLICY assets_org_policy ON assets
    FOR ALL
    TO public
    USING (org_id = (SELECT current_setting('app.org_id')::uuid));

-- This role only has access to SELECT queries on customer data tables.
CREATE ROLE customer_query_role NOLOGIN NOINHERIT;
GRANT SELECT ON assets TO customer_query_role;

